package com.github.viewmodel.vm

import androidx.lifecycle.MutableLiveData
import androidx.lifecycle.ViewModel
import com.github.viewmodel.model.User

class HelloViewModel : ViewModel() {
    var user = MutableLiveData<User>()

    init {
        user.postValue(User().apply {
            name.set("Kotlin")
            avatar.set("https://bkimg.cdn.bcebos.com/pic/9a504fc2d5628535746e08f997ef76c6a6ef6358?x-bce-process=image/resize,m_lfit,w_268,limit_1")
        })
    }

    fun showData() {
        val value = user.value
        value?.let {
            value.password.set("666")
            value.name.set("Jetpack")
            value.avatar.set("data:image/jpeg;base64,")
            user.postValue(value)
        }
    }
}